mapper.select:
SELECT * FROM tb_module
WHERE module_id = #{moduleId}
AND isaction = 'Y'

SELECT m.module_id as moduleId, m.module_name as moduleName, m.energy_type as energyType, m.rated_power as ratedPower, m.gateway_code as gatewayCode, m.meter_id as meterId, d.depart_name as dimensionName, d.id as dimensionId FROM tb_module m LEFT JOIN sys_depart d ON m.sys_org_code = d.id WHERE m.sys_org_code IN
#{dimensionId}
AND m.isaction = 'Y'
AND m.energy_type = #{energyType}
ORDER BY m.module_id
